mapper.select:
SELECT 

FROM sys_views a

WHERE a.id = #{id}

SELECT 

FROM sys_views a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT 

FROM sys_views a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_views SET 	
	foreign_id = #{foreignId},
	user_id = #{userId},
	ip = #{ip},
	token = #{token}
WHERE id = #{id}

UPDATE sys_views SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
