mapper.select:
SELECT 

FROM act_yw_gtime a

WHERE a.id = #{id}

SELECT 

FROM act_yw_gtime a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM act_yw_gtime a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
