mapper.select:
SELECT 

FROM sys_attachment a

WHERE a.id = #{id}

SELECT
FROM sys_attachment a
AND a.uid =#{uid}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_attachment SET 	

	uid = #{gid}

WHERE uid = #{oldGidId}

mapper.select:
select t.id, t.name arrName,t.url arrUrl,date_format(t.create_date,'%Y-%m-%d') create_date,t.suffix from sys_attachment t
where t.uid=#{uid}

and t.file_step=#{file_step}


	and t.type=#{type}

order by t.create_date

SELECT 

FROM sys_attachment a

WHERE a.url = #{url}
